// Create the chart object
let m = {
    type: 'line',
    options: {
        title: {
            display:true,
            text:'Pumphouse Temperatures'
        },
        legend: {
            display:false
        },
        chartArea: {
            backgroundColor: 'white'
        },
        plugins: {
            datalabels: {
                display:true,
                backgroundColor:'whitesmoke',
                borderRadius:1,
                padding:1,
                align: 'right',
                anchor: function(context) {
                    if (context.dataIndex == context.dataset.data.length - 1) {
                        return 'center';
                    } else {
                        return 'end';
                    }
                },
                offset:8,
                formatter:function(value) {
                    return value > 0 ? value.toLocaleString() : '';
                }
            }
        }
    },
    data: {
        labels:[],
        datasets: [
            {
                label:"Pumphouse Temperatures",
                borderColor:'rgba(57,97,184,0.8)',
                fill:false,
                data: msg.payload
            }
        ]
    }
}


msg.payload = m;

return msg;
